import React from "react";
import PropTypes from "prop-types";
import { Dropdown } from "office-ui-fabric-react";
import GraphViewerCommandBarComponent from "./GraphViewerCommandBarComponent/GraphViewerCommandBarComponent";
import {
  GraphViewerCytoscapeComponent,
  GraphViewerCytoscapeLayouts,
} from "./GraphViewerCytoscapeComponent/GraphViewerCytoscapeComponent";
import GraphViewerRelationshipCreateComponent from "./GraphViewerRelationshipCreateComponent/GraphViewerRelationshipCreateComponent";
import { GraphViewerRelationshipViewerComponent } from "./GraphViewerRelationshipViewerComponent/GraphViewerRelationshipViewerComponent";
import { GraphViewerTwinDeleteComponent } from "./GraphViewerTwinDeleteComponent/GraphViewerTwinDeleteComponent";
import GraphViewerRelationshipDeleteComponent from "./GraphViewerRelationshipDeleteComponent/GraphViewerRelationshipDeleteComponent";
import PropertyInspectorComponent from "../PropertyInspectorComponent/PropertyInspectorComponent";
import GraphViewerFilteringComponent from "./GraphViewerFilteringComponent/GraphViewerFilteringComponent";
import { withTranslation } from "react-i18next";

import LoaderComponent from "../LoaderComponent/LoaderComponent";
import { apiService } from "../../services/ApiService";
import { ModelService } from "../../services/ModelService";
import { eventService } from "../../services/EventService";
import { print } from "../../services/LoggingService";
import { BatchService } from "../../services/BatchService";
import { settingsService } from "../../services/SettingsService";

import "./GraphViewerComponent.scss";

const DEFAULT_DISPLAY_NAME_PROPERTY = "Default";

// eslint-disable-next-line no-redeclare
class GraphViewerComponent extends React.Component {

  renderDisplayNameSelectionItem = () => {
    const options = [
      {
        text: DEFAULT_DISPLAY_NAME_PROPERTY,
        key: DEFAULT_DISPLAY_NAME_PROPERTY
      },
      ...(this.props.displayNameProperties || []).map(property => ({
        key: property.displayName,
        text: property.displayName
      }))
    ];

    return (
      <Dropdown
        label="Display Name"
        options={options}
        selectedKey={
          this.props.selectedDisplayNameProperty
          || DEFAULT_DISPLAY_NAME_PROPERTY
        }
        onChange={this.props.onDisplayNamePropertyChanged} />
    );
  };

  render() {
    const {
      layouts,
      layout,
      onLayoutChanged,
      onClearGraph,
      onShowAll,
      canShowAll
    } = this.props;

    return (
      <div className="graph-viewer-command-bar">
        <div className="command-bar-section">
          <Dropdown
            label="Layout"
            options={layouts.map(layoutName => ({
              key: layoutName,
              text: layoutName
            }))}
            selectedKey={layout}
            onChange={(event, option) => onLayoutChanged(option.key)} />
        </div>
        <div className="command-bar-section">
          {this.renderDisplayNameSelectionItem()}
        </div>
        <div className="command-bar-section">
          {/* Explicit type="button" added to fix eslint warning */}
          <button
            type="button"
            onClick={onShowAll}
            disabled={!canShowAll}
            // eslint-disable-next-line no-negated-condition
            className={`command-bar-button ${!canShowAll ? "disabled" : ""}`}
            aria-disabled={!canShowAll}>
            Show All
          </button>
          <button
            type="button"
            onClick={onClearGraph}
            className="command-bar-button">
            Clear Graph
          </button>
        </div>
      </div>
    );
  }

}

// eslint-disable-next-line react/static-property-placement
GraphViewerCommandBarComponent.defaultProps = {
  displayNameProperties: [],
  selectedDisplayNameProperty: DEFAULT_DISPLAY_NAME_PROPERTY,
  // eslint-disable-next-line react/jsx-sort-default-props, no-empty-function
  onDisplayNamePropertyChanged: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types
  layouts: [],
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types
  layout: "Default",
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types, no-empty-function
  onLayoutChanged: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types, no-empty-function
  onClearGraph: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types, no-empty-function
  onShowAll: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types
  canShowAll: false
};

// eslint-disable-next-line react/static-property-placement
GraphViewerCommandBarComponent.propTypes = {
  displayNameProperties: PropTypes.arrayOf(
    PropTypes.shape({
      displayName: PropTypes.string.isRequired
    })
  ),
  selectedDisplayNameProperty: PropTypes.string,
  // eslint-disable-next-line react/sort-prop-types
  onDisplayNamePropertyChanged: PropTypes.func,
  // eslint-disable-next-line react/sort-prop-types
  layouts: PropTypes.arrayOf(PropTypes.string).isRequired,
  // eslint-disable-next-line react/sort-prop-types
  layout: PropTypes.string.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onLayoutChanged: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onClearGraph: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onShowAll: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  canShowAll: PropTypes.bool.isRequired
};

export default GraphViewerCommandBarComponent;
