import React from "react";
import PropTypes from "prop-types";
import { Dropdown } from "office-ui-fabric-react";
import "./GraphViewerCommandBarComponent.scss";

const DEFAULT_DISPLAY_NAME_PROPERTY = "Default";

class GraphViewerCommandBarComponent extends React.Component {

  renderDisplayNameSelectionItem = () => {
    const options = [
      {
        text: DEFAULT_DISPLAY_NAME_PROPERTY,
        key: DEFAULT_DISPLAY_NAME_PROPERTY
      },
      ...(this.props.displayNameProperties || []).map(property => ({
        key: property.displayName,
        text: property.displayName
      }))
    ];

    return (
      <Dropdown
        label="Display Name"
        options={options}
        selectedKey={
          this.props.selectedDisplayNameProperty
          || DEFAULT_DISPLAY_NAME_PROPERTY
        }
        onChange={this.props.onDisplayNamePropertyChanged} />
    );
  };

  render() {
    const {
      layouts,
      layout,
      onLayoutChanged,
      onClearGraph,
      onShowAll,
      canShowAll
    } = this.props;

    return (
      <div className="graph-viewer-command-bar">
        <div className="command-bar-section">
          <Dropdown
            label="Layout"
            options={layouts.map(layoutName => ({
              key: layoutName,
              text: layoutName
            }))}
            selectedKey={layout}
            onChange={(event, option) => onLayoutChanged(option.key)} />
        </div>
        <div className="command-bar-section">
          {this.renderDisplayNameSelectionItem()}
        </div>
        <div className="command-bar-section">
          <button onClick={onShowAll} disabled={!canShowAll}>
            Show All
          </button>
          <button onClick={onClearGraph}>Clear Graph</button>
        </div>
      </div>
    );
  }

}

// Default props to handle undefined or missing props
GraphViewerCommandBarComponent.defaultProps = {
  displayNameProperties: [], // Default to an empty array if not provided
  selectedDisplayNameProperty: DEFAULT_DISPLAY_NAME_PROPERTY, // Default selected property
  onDisplayNamePropertyChanged: () => {}, // Default to a no-op function
  layouts: [],
  layout: "Default",
  onLayoutChanged: () => {},
  onClearGraph: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types
  onShowAll: () => {},
  // eslint-disable-next-line react/jsx-sort-default-props, react/default-props-match-prop-types
  canShowAll: false
};

// Prop validation to ensure proper usage
// eslint-disable-next-line react/static-property-placement
GraphViewerCommandBarComponent.propTypes = {
  displayNameProperties: PropTypes.arrayOf(
    PropTypes.shape({
      displayName: PropTypes.string.isRequired
    })
  ),
  selectedDisplayNameProperty: PropTypes.string,
  // eslint-disable-next-line react/sort-prop-types
  onDisplayNamePropertyChanged: PropTypes.func,
  // eslint-disable-next-line react/sort-prop-types
  layouts: PropTypes.arrayOf(PropTypes.string).isRequired,
  // eslint-disable-next-line react/sort-prop-types
  layout: PropTypes.string.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onLayoutChanged: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onClearGraph: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  onShowAll: PropTypes.func.isRequired,
  // eslint-disable-next-line react/sort-prop-types
  canShowAll: PropTypes.bool.isRequired
};

export default GraphViewerCommandBarComponent;
